sysauto_script.script:
var rightNow = new GlideDateTime();

// Query the database for NeedIt task records due between now and 24 hours from now. Only return NeedIt task records that do not have a state of Closed Complete.
var dueSoon = new GlideRecord('x_58872_needit_needit_task');
dueSoon.addQuery('due_date', '>=', rightNow);
dueSoon.addQuery('due_date', '<', gs.hoursAgo(-24));
dueSoon.addQuery('state', '!=', 3);

dueSoon.query();
// Write a log message for each of the NeedIt task records due within the next 24 hours.
while (dueSoon.next()) {
    gs.info("NeedIt Task due soon record = " + dueSoon.number);
    gs.eventQueue('x_58872_needit.dueSoonNeedItTasks', dueSoon, dueSoon.number, gs.getUserName());
}
